package com.atc.common.model;/** * Created by Vic.Feng on 23/11/2015. */public class CommonResultModel {    private int code;    private String message;    public CommonResultModel() {        this.code = 0;        this.message = "";    }    public CommonResultModel(int code, String message) {        this.code = code;        this.message = message;    }    public int getCode() {        return code;    }    public void setCode(int code) {        this.code = code;    }    public String getMessage() {        return message;    }    public void setMessage(String message) {        this.message = message;    }}