package com.atc.common.model;import java.util.List;/** * Created by Vic.Feng on 19/11/2015. */public class ListResultModel<T> extends CommonResultModel {    private long totalRecords;    private List<T> data;    public ListResultModel() {    }    public ListResultModel(int code, String message) {        super(code, message);    }    public long getTotalRecords() {        return totalRecords;    }    public void setTotalRecords(long totalRecords) {        this.totalRecords = totalRecords;    }    public List<T> getData() {        return data;    }    public void setData(List<T> data) {        this.data = data;    }}