package com.atc.common.model.home;/** * Created by Vic.Feng on 30/11/2015. */public class LoginInfo {    private String organisation;    private String password;    private String username;    public LoginInfo() {    }    public String getOrganisation() {        return organisation;    }    public void setOrganisation(String organisation) {        this.organisation = organisation;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }    public String getUsername() {        return username;    }    public void setUsername(String username) {        this.username = username;    }}