package com.atc.common.util;import com.atc.common.constant.SystemConstants;import javax.servlet.http.HttpServletRequest;/** * Created by Vic.Feng on 05/12/2015. */public class CommonUtil {    /**     * Get Country Code     *     * @param request Http Servlet Request     * @return Country Code     */    public static String getCountryCode(HttpServletRequest request) {        Object obj = request.getSession().getAttribute(SystemConstants.LANGUAGE);        if (obj != null) {            return obj.toString();        } else {            return SystemConstants.LANGUAGE_CODE_EN;        }    }}