package com.atc.common.util;import java.io.UnsupportedEncodingException;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;/** * Created by Vic.Feng on 02/12/2015. */public class EncryptUtil {    /**     * Encrypt A String With MD5     *     * @param str String Need to Be Encrypted     * @return Encrypted String     */    public static String getMD5(String str) {        MessageDigest messageDigest = null;        try {            messageDigest = MessageDigest.getInstance("MD5");            messageDigest.reset();            messageDigest.update(str.getBytes("UTF-8"));        } catch (NoSuchAlgorithmException e) {            e.printStackTrace();        } catch (UnsupportedEncodingException e) {            e.printStackTrace();        }        byte[] byteArray = messageDigest.digest();        StringBuffer stringBuffer = new StringBuffer();        for (int i = 0; i < byteArray.length; i++) {            if (Integer.toHexString(0xFF & byteArray[i]).length() == 1)                stringBuffer.append("0").append(Integer.toHexString(0xFF & byteArray[i]));            else                stringBuffer.append(Integer.toHexString(0xFF & byteArray[i]));        }        return stringBuffer.toString();    }}