package com.atc.domains.home.entity;import com.atc.common.model.BasePrimaryID;import javax.persistence.*;import java.io.Serializable;/** * Created by Vic.Feng on 30/11/2015. */@Entity@IdClass(BasePrimaryID.class)@Table(name = "actors")public class Actor implements Serializable {    @Id    @Column(name="ORG")    private String org;    @Id    @Column(name="KEY_ID")    private Integer keyId;    @Column(name="LOGIN_NAME")    private String loginName;    @Column(name="PASSWORD")    private String password;    public Actor() {    }    public String getOrg() {        return org;    }    public void setOrg(String org) {        this.org = org;    }    public Integer getKeyId() {        return keyId;    }    public void setKeyId(Integer keyId) {        this.keyId = keyId;    }    public String getLoginName() {        return loginName;    }    public void setLoginName(String loginName) {        this.loginName = loginName;    }    public String getPassword() {        return password;    }    public void setPassword(String password) {        this.password = password;    }}