package com.atc.domains.home.entity;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.Id;import javax.persistence.Table;import java.io.Serializable;/** * Created by Vic.Feng on 30/11/2015. */@Entity@Table(name = "task")public class Task implements Serializable {    @Id    @GeneratedValue    private Long taskId;    private String description;    private String location;    private String slaDate;    private String manager;    private String resolver;    private String priority;    private String status;    private String type;    public Task() {    }    public Long getTaskId() {        return taskId;    }    public void setTaskId(Long taskId) {        this.taskId = taskId;    }    public String getDescription() {        return description;    }    public void setDescription(String description) {        this.description = description;    }    public String getLocation() {        return location;    }    public void setLocation(String location) {        this.location = location;    }    public String getSlaDate() {        return slaDate;    }    public void setSlaDate(String slaDate) {        this.slaDate = slaDate;    }    public String getManager() {        return manager;    }    public void setManager(String manager) {        this.manager = manager;    }    public String getResolver() {        return resolver;    }    public void setResolver(String resolver) {        this.resolver = resolver;    }    public String getPriority() {        return priority;    }    public void setPriority(String priority) {        this.priority = priority;    }    public String getStatus() {        return status;    }    public void setStatus(String status) {        this.status = status;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }}