package com.atc.service.home.impl;import com.atc.domains.home.ITaskDAO;import com.atc.domains.home.entity.Task;import com.atc.service.home.ITaskService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;/** * Created by Vic.Feng on 30/11/2015. */@Servicepublic class TaskServiceImpl implements ITaskService {    private final Logger logger = LoggerFactory.getLogger(TaskServiceImpl.class);    @Autowired    private ITaskDAO taskDAO;    /**     * Get all tasks     *     * @return     */    public List<Task> getAllTasks() {        try {            return taskDAO.selectTasks();        } catch (Exception ex) {            logger.error(ex.getMessage());        }        return null;    }}