package com.atc.service.home.impl;import com.atc.common.model.home.LoginInfo;import com.atc.common.util.EncryptUtil;import com.atc.domains.home.IUserDAO;import com.atc.domains.home.entity.User;import com.atc.service.home.IUserService;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;/** * Created by Vic.Feng on 30/11/2015. */@Servicepublic class UserServiceImpl implements IUserService {    private final Logger logger = LoggerFactory.getLogger(UserServiceImpl.class);    @Autowired    private IUserDAO userDAO;    /**     * Validate user info     *     * @param loginInfo     * @return user object     */    public User validateUser(LoginInfo loginInfo) {        try {            String encryptPassword = EncryptUtil.getMD5(loginInfo.getPassword());            return userDAO.selectUserByNameAndPassword(loginInfo.getUsername(), encryptPassword, loginInfo.getOrganisation());        } catch (Exception ex) {            logger.error(ex.getMessage());        }        return null;    }}