package com.atc.web;import com.atc.common.util.DoubleEditor;import org.springframework.beans.propertyeditors.CustomDateEditor;import org.springframework.web.bind.WebDataBinder;import org.springframework.web.bind.annotation.InitBinder;import java.text.SimpleDateFormat;import java.util.Date;/** * Created by Vic.Feng on 29/11/2015. */public abstract class AbstractController {    /**     * Init Binder     *     * @param bin Web Data Binder Object     */    @InitBinder    public void initBinder(WebDataBinder bin) {        SimpleDateFormat simpleDateFormat = new SimpleDateFormat("yyyy-MM-dd");        CustomDateEditor customDateEditor = new CustomDateEditor(simpleDateFormat, true);        bin.registerCustomEditor(Date.class, customDateEditor);        bin.registerCustomEditor(double.class, new DoubleEditor());    }}