package com.atc.web.controller.group;import com.atc.domains.group.entity.Group;import com.atc.service.group.IGroupService;import com.atc.web.AbstractController;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import java.util.List;/** * Created with IntelliJ IDEA. * User: Vic.Feng * Date: 09/11/15 * Time: 16:04 * To change this template use File | Settings | File Templates. */@Controllerpublic class GroupController extends AbstractController {    private final Logger logger = LoggerFactory.getLogger(GroupController.class);    @Autowired    IGroupService groupService;    /**     * param     * @return  grouplist     */    @RequestMapping("/groups")    @ResponseBody    public List<Group> getGroupList() {        List<Group> groupList = groupService.getGroupList();        return groupList;    }}