package com.atc.web.controller.group;import com.atc.common.util.SessionUtil;import com.atc.domains.group.entity.Group;import com.atc.domains.home.entity.Actor;import com.atc.service.group.IGroupService;import com.atc.web.AbstractController;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import javax.servlet.http.HttpServletRequest;import java.util.List;/** * Created with IntelliJ IDEA. * User: Vic.Feng * Date: 09/11/15 * Time: 16:04 * To change this template use File | Settings | File Templates. */@Controllerpublic class GroupController extends AbstractController {    private final Logger logger = LoggerFactory.getLogger(GroupController.class);    @Autowired    IGroupService groupService;    /**     * Get asset group list     *     * @return asset group list     */    @RequestMapping("/groups")    @ResponseBody    public List<Group> getGroupList(HttpServletRequest request) {        Actor user = SessionUtil.getSessionUser(request);        List<Group> groupList = groupService.getGroupList(user.getOrg());        return groupList;    }}