package com.atc.web.controller.location;import com.atc.domains.location.entity.Location;import com.atc.service.location.ILocationService;import com.atc.web.AbstractController;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.*;import java.util.List;/** * Created with IntelliJ IDEA. * User: Vic.Feng * Date: 09/11/15 * Time: 16:04 * To change this template use File | Settings | File Templates. */@Controllerpublic class LocationController extends AbstractController {    private final Logger logger = LoggerFactory.getLogger(LocationController.class);    @Autowired    ILocationService locationService;    @RequestMapping(value = "/locations")    @ResponseBody    public List<Location> getLocations() {        return locationService.getLocations();    }}