package com.atc.web.controller.openTask;import com.atc.common.constant.ResultCodes;import com.atc.common.model.ListResultModel;import com.atc.domains.home.entity.Task;import com.atc.service.home.ITaskService;import com.atc.service.home.IUserService;import com.atc.web.AbstractController;import org.slf4j.Logger;import org.slf4j.LoggerFactory;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.ResponseBody;import org.springframework.web.servlet.ModelAndView;/** * Created with IntelliJ IDEA. * User: Vic.Feng * Date: 09/11/15 * Time: 16:04 * To change this template use File | Settings | File Templates. */@Controllerpublic class OpenTaskController extends AbstractController {    private final Logger logger = LoggerFactory.getLogger(OpenTaskController.class);    @Autowired    ITaskService taskService;    /**     * Display List Page     *     * @return List Page     */    @RequestMapping(value = "/openTasksPage")    public ModelAndView index() {        return new ModelAndView("openTask/openTaskList");    }    /**     * Get Open Tasks     *     * @return Task List     */    @RequestMapping(value = "/openTasks")    @ResponseBody    public ListResultModel<Task> getOpenTasks() {        ListResultModel<Task> result = new ListResultModel();        result.setCode(ResultCodes.SUCCESS);        result.setData(taskService.getAllTasks());        return result;    }}